function rate(e){let o=document.querySelector("#rate-good"),c=document.querySelector("#rate-bad");o.checked=e,c.checked=!e,showThanks(e),showFeedbackForm(!e)}function showFeedbackForm(e){let o=document.querySelector("#feedback-bad-container");e?o.classList.remove("none"):o.classList.add("none")}function showThanks(e){let o=document.querySelector("#feedback-thanks-container");e?o.classList.remove("none"):o.classList.add("none")}