suite("lunr.stemmer",function(){test("reduces words to their stem",function(e){withFixture("stemming_vocab.json",function(t,r){if(null!=t)throw t;var n=JSON.parse(r);Object.keys(n).forEach(function(e){var t=n[e],r=new lunr.Token(e),s=lunr.stemmer(r).toString();assert.equal(t,s)}),e()})}),test("is a registered pipeline function",function(){assert.equal("stemmer",lunr.stemmer.label),assert.equal(lunr.stemmer,lunr.Pipeline.registeredFunctions.stemmer)})});